<script>
	import { animate, signal } from '../../motion';
	const note1 = signal({ x: 2080, y: 921, width: 49, height: 56, opacity: 0 });
	const note2 = signal({ x: 2080, y: 921, width: 41, height: 49, opacity: 0 });
	const note3 = signal({ x: 2080, y: 921, width: 18, height: 45, opacity: 0 });
	const note4 = signal({ x: 2080, y: 921, width: 92, height: 71, opacity: 0 });
	const note5 = signal({ x: 2080, y: 921, width: 49, height: 56, opacity: 0 });

	animate(async () => {
		while (true) {
			await note1
				.to({ x: 2080, y: 621, width: 49, height: 56, opacity: 1 }, { duration: 3000 })
				.to({ opacity: 0 })
				.to({ x: 2080, y: 921, width: 49, height: 56, opacity: 0 });

			await note2
				.to({ x: 2080, y: 621, width: 41, height: 49, opacity: 1 }, { duration: 3000 })
				.to({ opacity: 0 })
				.to({ x: 2080, y: 921, width: 41, height: 49, opacity: 0 });

			await note3
				.to({ x: 2080, y: 621, width: 18, height: 45, opacity: 1 }, { duration: 3000 })
				.to({ opacity: 0 })
				.to({ x: 2080, y: 921, width: 18, height: 45, opacity: 0 });

			await note4
				.to({ x: 2080, y: 621, width: 92, height: 71, opacity: 1 }, { duration: 3000 })
				.to({ opacity: 0 })
				.to({ x: 2080, y: 921, width: 92, height: 71, opacity: 0 });

			await note5
				.to({ x: 2080, y: 621, width: 49, height: 56, opacity: 1 }, { duration: 3000 })
				.to({ opacity: 0 })
				.to({ x: 2080, y: 921, width: 49, height: 56, opacity: 0 });
		}
	});
</script>

<image
	id="Notes5"
	x="2080"
	y={$note5.y}
	width="49"
	height="56"
	style="opacity:{$note5.opacity}"
	xlink:href="data:img/png;base64,"
/>
<image
	id="Notes4"
	x="2080"
	y={$note4.y}
	width="92"
	height="71"
	style="opacity:{$note4.opacity}"
	xlink:href="data:img/png;base64,"
/>
<image
	id="Notes3"
	x="2080"
	y={$note3.y}
	width="18"
	height="45"
	style="opacity:{$note3.opacity}"
	xlink:href="data:img/png;base64,"
/>
<image
	id="Notes2"
	x="2080"
	y={$note2.y}
	width="41"
	height="49"
	style="opacity:{$note2.opacity}"
	xlink:href="data:img/png;base64,"
/>
<image
	id="Notes1"
	width="42"
	height="77"
	x="2080"
	y={$note1.y}
	style="opacity:{$note1.opacity}"
	xlink:href="data:img/png;base64,"
/>
